import React, { useState, useEffect } from 'react'
import ReactDOM from 'react-dom'
import Axios from 'axios'
import { MdCameraAlt } from 'react-icons/md'
import { devUrl } from '../../config'
import { withRouter } from 'react-router-dom'
function MemberPhoto(props) {
  const [member, setMember] = useState([])
  const [member_img, setMemberImg] = useState([])
  const [member_id, setMemberId] = useState('')
  const [Update_submit, SetUpdateSubmit] = useState('')

  useEffect(() => {
    Axios.get(`http://localhost:3001/member/get/${props.match.params.id}`).then(
      (res) => {
        setMember(res.data)
      }
    )
  }, [member])

  function ultimateUpdate(e) {
    setMemberImg(e.target.value.split('\\').pop())
    UpdateMemberImg()
  }
  const UpdateMemberImg = (member_id) => {
    console.log(member_img)

    Axios.put('http://localhost:3001/member/update/img', {
      member_id: member_id,
      member_img: member_img,
    }).then((response) => {
      setMember(
        member.map((val) => {
          return val.member_id === member_id
            ? {
                member_id: val.member_id,
                member_img: val.member_img,
              }
            : val
        })
      )
    })
  }
  return (
    <>
      {member.map((val) => {
        return (
          <div className="d-flex justify-content-center align-items-center">
            <div className="mem_img_box ">
              <img
                className="mem_img rounded-circle"
                style={{ width: '160px', height: '160px', cursor: ' pointer ' }}
                variant="top"
                src={devUrl + `/pic/mem_img/${val.member_img}`}
                alt=""
                id="submitmemphoto-button-file"
              />
            </div>

            <div class="position-relative">
              <input
                accept="image/*"
                className=""
                style={{ display: 'none' }}
                id="iconmemphoto-button-file"
                type="file"
                onChange={ultimateUpdate(e, val.member_id)}
              />
              <button
                style={{ display: 'none' }}
                id="submitmemphoto-button-file"
                onClick={() => {
                  UpdateMemberImg(val.member_id)
                }}
              >
                上傳
              </button>
              <label htmlFor="iconmemphoto-button-file">
                <MdCameraAlt
                  aria-label="upload picture"
                  component="span"
                  className=" position-absolute  translate-middle "
                  style={{
                    width: '42px',
                    height: '42px',
                    top: '40px',
                    right: '-5px',
                    color: '#104b6d',
                    cursor: ' pointer ',
                  }}
                />
              </label>
            </div>
          </div>
        )
      })}
    </>
  )
}
export default withRouter(MemberPhoto)
