import React, { useState, useEffect } from 'react'
import Axios from 'axios'
import '../../style/default.scss'
import { Card, Form, Button } from 'react-bootstrap'
import '../../style/member/member_content.scss'
import { MdVisibility, MdDone } from 'react-icons/md'
import { withRouter, Link } from 'react-router-dom'
import { devUrl } from '../../config'
function MemberContent(props) {
  const [member, setMember] = useState([])
  const [member_name, setMembername] = useState('')
  const [member_gender, setMembergender] = useState('')
  const [member_phone, setMemberphone] = useState('')
  const [member_birthday, setMemberbirthday] = useState('')
  const [member_ex, setMemberex] = useState('')
  const [member_about, setMemberabout] = useState('')

  useEffect(() => {
    Axios.get(`http://localhost:3001/member/get/${props.match.params.id}`)
      .then((res) => {
        setMember(res.data)
      })
      .catch((error) => {
        console.error(error)
      })
  }, [])

  const UpdateMember = (member_id) => {
    Axios.put('http://localhost:3001/member/update', {
      member_id: member_id,
      member_name: member_name,
      member_phone: member_phone,
      member_birthday: member_birthday,
      member_ex: member_ex,
      member_about: member_about,
    }).then((response) => {
      setMember()

      // member.map((val) => {
      //   return val.member_id === member_id
      //     ? {
      //         member_id: val.member_id,
      //         member_name: val.member_name,
      //         member_gender: val.member_gender,
      //         member_phone: val.member_phone,
      //         member_birthday: val.member_birthday,
      //         member_ex: val.member_ex,
      //         member_about: val.member_about,
      //       }
      //     : val
      // })
    })
  }

  function dateConvert(jsonDate) {
    let json = String(jsonDate).split(' ')

    let date = new Date(json[0])
    let dd = date.getDate()
    let mm = date.getMonth() + 1

    var yyyy = date.getFullYear()
    if (dd < 10) {
      dd = '0' + dd
    }
    if (mm < 10) {
      mm = '0' + mm
    }
    date = yyyy + '-' + mm + '-' + dd
    return date
  }

  return (
    <>
      {member.map((val) => {
        return (
          <div className="container ">
            <Card className="mem_box">
              <Card.Header className="mem_title d-flex justify-content-between">
                <h5>會員資料</h5>

                <Button
                  onClick=""
                  className="btn-style botton-font btn_icon"
                  href={devUrl + `/see/${props.match.params.id}`}
                >
                  <MdVisibility />
                  檢視
                </Button>
              </Card.Header>
              <Card.Body style={{ padding: '0  38px  43px 42px' }}>
                <Form className="row  d-flex justify-content-start mem_inf">
                  <Form.Group controlId="formBasicEmail" className="mem_form">
                    <Form.Label className="d-flex ">
                      <p className="subtitle1" style={{ color: '#df3d00' }}>
                        *
                      </p>
                      <p className="subtitle1">帳號</p>
                    </Form.Label>

                    <Form.Control
                      type="text"
                      placeholder="帳號"
                      className="  text_w"
                      name="member_account"
                      value={val.member_account}
                      readOnly
                    />
                  </Form.Group>
                  <Form.Group controlId="formBasicEmail" className="mem_form">
                    <Form.Label className="d-flex">
                      <p className="subtitle1" style={{ color: '#df3d00' }}>
                        *
                      </p>
                      <p className="subtitle1">名稱</p>
                    </Form.Label>
                    <Form.Control
                      type="name"
                      placeholder="名稱"
                      className="text_w"
                      name="member_name"
                      Value={val.member_name}
                      onChange={(e) => {
                        setMembername(e.target.value)
                      }}
                    />
                  </Form.Group>
                  <Form.Group controlId="formBasicEmail" className="mem_form">
                    <Form.Label className="d-flex">
                      <p className="subtitle1" style={{ color: '#df3d00' }}>
                        *
                      </p>
                      <p className="subtitle1">信箱</p>
                    </Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="信箱"
                      className=" text_w"
                      name="member_email"
                      value={val.member_email}
                      readOnly
                    />
                  </Form.Group>
                  <Form.Group>
                    <Form.Label className="d-flex mem_form">
                      <p className="subtitle1" style={{ color: '#df3d00' }}>
                        *
                      </p>
                      <p className="subtitle1">性別</p>
                    </Form.Label>

                    <Form.Label className="mem_form ">
                      <div className="d-flex text_w">
                        <Form.Check
                          type="radio"
                          label="男"
                          Value="男"
                          defaultChecked={`${val.member_gender}` === '男'}
                          // disabled={`${val.member_gender}` === '男'}
                          name="
                    formHorizontalRadio"
                          id="formHorizontalRadios1"
                          onChange={(e) => {
                            setMembergender(e.target.value)
                          }}
                        />
                        <Form.Check
                          type="radio"
                          label="女"
                          Value="女"
                          defaultChecked={`${val.member_gender}` === '女'}
                          // disabled={`${val.member_gender}` === '女'}
                          name="
                    formHorizontalRadio"
                          id="formHorizontalRadios2"
                          onChange={(e) => {
                            setMembergender(e.target.value)
                          }}
                        />
                      </div>
                    </Form.Label>
                  </Form.Group>

                  <Form.Group controlId="formBasicEmail" className="mem_form">
                    <Form.Label>生日</Form.Label>
                    <Form.Control
                      type="date"
                      placeholder=""
                      className="text_w"
                      Value={dateConvert(val.member_birthday)}
                      onChange={(e) => {
                        setMemberbirthday(e.target.value)
                      }}
                    />
                  </Form.Group>

                  {/* <DateConvert jsonDate={val.member_birthday} /> */}
                  <Form.Group
                    controlId="exampleForm.ControlSelect1"
                    className="mem_form"
                  >
                    <Form.Label>攝影經驗</Form.Label>

                    <Form.Control
                      as="select"
                      className="  text_w"
                      name="member_ex"
                      Value={val.member_ex}
                      onChange={(e) => {
                        setMemberex(e.target.value)
                      }}
                    >
                      <option className="subtitle1" value="0">
                        一年以下
                      </option>
                      <option className="subtitle1" value="1">
                        一年
                      </option>
                      <option className="subtitle1" value="3">
                        兩年
                      </option>
                      <option className="subtitle1" value="4">
                        四年
                      </option>
                      <option className="subtitle1" value="5">
                        五年
                      </option>
                      <option className="subtitle1" value="6">
                        六年
                      </option>
                      <option className="subtitle1" value="7">
                        七年
                      </option>
                      <option className="subtitle1" value="8">
                        七年以上
                      </option>
                    </Form.Control>
                  </Form.Group>
                  <Form.Group controlId="formBasicEmail" className="mem_form">
                    <Form.Label>電話</Form.Label>

                    <Form.Control
                      type="photo"
                      placeholder="請輸入電話"
                      className="text_w"
                      Value={val.member_phone}
                      name="member_phone"
                      onChange={(e) => {
                        setMemberphone(e.target.value)
                      }}
                    />
                  </Form.Group>
                  <Form.Group controlId="formBasicEmail" className="mem_form">
                    <Form.Label>關於我</Form.Label>
                    <Form.Control
                      type="text"
                      // as="textarea"
                      rows={2}
                      placeholder="關於我"
                      className="text_w"
                      Value={val.member_about}
                      name="member_about"
                      onChange={(e) => {
                        setMemberabout(e.target.value)
                      }}
                    />
                  </Form.Group>
                </Form>
                <div className="d-flex justify-content-end">
                  <Button
                    type="submit"
                    className="btn-style botton-font btn_icon "
                    onClick={() => {
                      UpdateMember(val.member_id)
                    }}
                  >
                    <MdDone />
                    儲存
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </div>
        )
      })}
    </>
  )
}

export default withRouter(MemberContent)
